package com.aosica.openapi.config;import org.springframework.context.annotation.Bean;import org.springframework.stereotype.Component;import java.util.concurrent.ExecutorService;import java.util.concurrent.Executors;@Componentpublic class ThreadPoolConfig {    @Bean    public ExecutorService executorService() {        ExecutorService executorService = Executors.newCachedThreadPool();        return executorService;    }}